'use strict';

/**
 * Geometry
 */
class Geometry {
  /**
   * Geometry constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};
    this.x = opt.x || 0;
    this.y = opt.y || 0;
    this.z = opt.z || 0;
    this.x1 = opt.x1 || 0;
    this.y1 = opt.y1 || 0;
    this.x2 = opt.x2 || 0;
    this.y2 = opt.y2 || 0;
    this.wireFlags = opt.wireFlags || '';
  }
}

module.exports = Geometry;

'use strict';

const Geometry = require('./geometry');

/**
 *
 */
class FZInstance {
  /**
   * the FZInstance constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};
    this.moduleIdRef = opt.moduleIdRef || '';
    this.modelIndex = opt.modelIndex || '';
    this.path = opt.path || '';
    this.property = opt.property || [];
    this.title = opt.title || '';
    this.views = {
      breadboard: [],
      pcb: [],
      schematic: [],
    };
  }
}

/**
 * FZInstanceView
 */
class FZInstanceView {
  /**
   * FZInstanceView constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};
    this.layer = opt.layer || '';
    this.bottom = opt.bottom || '';
    this.geometry = new Geometry(opt.geometry) || new Geometry();
    this.wireExtra = opt.wireExtra || '';
    this.connectors = opt.connectors || '';
  }
}

module.exports = {FZInstance, FZInstanceView};

'use strict';

/**
 * FZLeg for breadboard views.
 */
class FZLeg {
  /**
   * FZLeg constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};
    this.point = new Vector2D(opt.point);
    this.bezier = opt.bezier || null;
  }
}

module.exports = FZLeg;

'use strict';

/**
 * FZProgram
 */
class FZProgram {
  /**
   * the FZProgram constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};
    this.language = opt.language || null;
    this.source = opt.source || null;
  }
}

module.exports = FZProgram;

'use strict';

/**
 * Vector2D is a general geometry structure we use for all coordinates.
 * WE can replace the class with a full featured math vector module later.
 */
class Vector2D {
  /**
   * Vector2D constructor
   * @param {Object} opt
   */
  constructor(opt) {
    opt = opt || {};

    /**
     * The x position
     * @type {Number}
     */
    this.x = opt.x || 0;

    /**
     * The y position
     * @type {Number}
     */
    this.y = opt.y || 0;
  }
}

module.exports = Vector2D;

